public class T1_First {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int side = 3;
		double square = side*side*(Math.sqrt(3)/4);
		System.out.println("Пл. пр. треугольника со стороной " +side+ " равна " +square);
		System.out.println("Пл. пр. шестиугольника со стороной " +side+ " равна " +square*6);

	}

}
